package com.lumen.training;

public class Greater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 30;
		int y = 40;
		int z = 50;
		if (x>y && x>z)System.out.println(x);
		if(y>z)System.out.println(y);
		else System.out.println(z);
		
	}

}
